import React from "react";

class EditFishForm extends React.Component {
    render(){
        return (
            <form className="fish-edit" onSubmit={this.createFish}>
                <input name="name" value={this.props.fish.name} ref={this.nameRef} type="text" placeholder="Name" />
                <input name="price" value={this.props.fish.price} ref={this.priceRef} type="text" placeholder="Price" />
                <select name="status" value={this.props.fish.status} ref={this.statusRef}>
                    <option value="available">Flesh!</option>
                    <option value="unavailable">Sold Out!</option>
                </select>
                <textarea name="desc" value={this.props.fish.desc} ref={this.descRef} type="text" placeholder="Desc" />
                <input name="image" value={this.props.fish.image} ref={this.imageRef} type="text" placeholder="Image" />
                <button type="submit">+ Edit Fish</button>
            </form>
        )
    }
}

export default EditFishForm;
